create database `drugInteraction`;
use `drugInteraction`;

CREATE TABLE `users` (
  `id` MEDIUMINT NOT NULL AUTO_INCREMENT,
  `username` varchar(32) COLLATE utf8mb4_bin NOT NULL UNIQUE,
  `name` mediumtext COLLATE utf8mb4_bin NOT NULL,
  `email` varchar(255) COLLATE utf8mb4_bin NOT NULL UNIQUE,
  `password` varchar(255) COLLATE utf8mb4_bin NOT NULL,
  `phone` varchar(255) COLLATE utf8mb4_bin NULL,
  `createdAt` datetime not NULL,
  `updatedAt` datetime not null,
  `updatedId` int null default 0,
  `deletedAt` datetime null,
  `deletedId` int null,
  `role` varchar(255) COLLATE utf8mb4_bin NOT NULL default 'user',
  `deleted` enum('true','false') not null default 'false',
  CONSTRAINT `users_pk` PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;


CREATE TABLE `search_text`(
  `idSearch` MEDIUMINT NOT NULL AUTO_INCREMENT,
  `searchId` int not NULL,
  `searchKey` text COLLATE utf8mb4_bin NOT NULL,
  `searchAt` datetime not null,
  CONSTRAINT `search_text_pk` PRIMARY KEY (`idSearch`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;

CREATE TABLE `hc_thuoc` (
