INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-265', 'HC-300', 2, 'Giảm tác động hạ huyết áp. Nếu cần dùng chung, theo dõi huyết áp và cân nhắc điều chỉnh liều của chẹn beta. ');
