INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-300', 'HC-374', 2, 'Tăng hoặc hạ đường huyết; che đậy các triệu chứng của hạ đường huyết. Tăng cường theo dõi nồng độ glucose hoặc điều chỉnh liều thuốc trị đái tháo đường.');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-310', 'HC-257', 2, 'Tăng đường huyết đói, giảm tác động hạ đường huyết của sulfonylure.Theo dõi chặt chẽ đường huyết. Cân nhắc tăng liều sulfonylure nếu bị tăng đường huyết.');
