INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-392', 'HC-274', 4, 'Tăng nguy cơ tiêu cơ vân. Nếu cần dùng macrolid, ngưng simvastatin/lovastatin trong thời gian điều trị.');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-421', 'HC-274', 3, 'Tăng nồng độ amlodipine, gây hạ huyết áp. Cân nhắc hiệu chỉnh liều và giám sát kĩ nồng độ nếu có thể, chú ý sự tăng các tác động phụ của amlodipine.');
