INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-461', 'HC-431', 3, 'Tránh dùng chung do làm giảm hiệu quả của clopidogrel. Ở bệnh nhân cần dùng PPI, thay thế omeprazole bằng PPI khác như pantoprazole.');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-82', 'HC-106', 2, 'NSAID có thể làm giảm tác động hạ huyết áp của ARB. Thận trọng nếu phối hợp. Theo dõi liệu lực hạ huyết áp và đánh giá chức năng thận định kì.');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-82', 'HC-310', 2, 'Trước khi dùng hydroCHLOROthiazide, hãy cho bác sĩ của bạn nếu bạn cũng sử dụng piroxicam. Bạn có thể cần điều chỉnh liều hoặc các xét nghiệm đặc biệt để có thể dùng cả hai loại thuốc cùng nhau một cách an toàn. Nếu bạn đang nhận được sự kết hợp này uống nhiều nước. Huyết áp và chức năng thận cũng nên được theo dõi. ');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-91', 'HC-374', 2, 'Tăng nguy cơ hạ đường huyết hoặc tăng đường huyết quá mức. Theo dõi đường huyết chặt chẽ và hiệu chỉnh liều thuốc trị đái tháo đường.');
INSERT INTO `tuongTac` (`idHc`, `idHcTuongTac`, `mucDo`, `noiDung`) VALUES ('HC-91', 'HC-578', 2, 'Tăng nguy cơ kính thích thần kinh trung ương và cơn co cứng co giật toàn thể, đặc biệt ở bệnh nhân có nguy cơ cao khởi phát cơn co giật.');
